INSERT INTO `cards` (`id`, `name`, `civilization`, `kind`, `power`, `cost`, `evolution`, `effects`, `trigger`, `str`) VALUES
(2911, '極頂秘伝ゼニス・シンフォニー', '5', '0', '', '9', '', '', '', 'アタック・チャンス－アンノウン<br/>自分のアンノウンとゼニスを種族に持つクリーチャーを1体、コストを支払わずに召喚してもよい。そのターンの終わりに、そのクリーチャーをバトルゾーンから自分の手札に戻す。'),
(2912, '勝利宣言鬼丸「覇」', '3', '58,108,109,107', '9000+', '10', '', '6,5', '2', 'スピードアタッカー<br/>パワーアタッカー+5000<br/>このクリーチャーが攻撃する時、相手とガチンコジャッジする。自分が勝ったら、このターンの後にもう一度自分のターンを行う。(ガチンコジャッジ：各プレイヤーは自身の山札の上から1枚目を見せ、それを一番下に置く。そのカードのコストが相手以上であれば、自分の勝ちとする)<br/>T・ブレイカー'),
(2913, '「呪」の頂 サスペンス', '5', '120,125', '16000', '12', '', '5', '', 'このクリーチャーを召喚してバトルゾーンに出した時、相手のシールドまたは手札からカードを2枚見ないで選ぶ。相手はそれを見せる。その中の呪文を、自分がコストを支払わずに唱え、持ち主の墓地に置いてもよい。その後、相手は見せたカードの残りを自身の墓地に置く。<br/>T・ブレイカー<br/>エターナル・Ω'),
(2914, '「祝」の頂 ウェディング', '5', '120,125', '13000', '11', '', '34,5', '', 'このクリーチャーを召喚してバトルゾーンに出した時、相手はバトルゾーンの自分のクリーチャーまたは自身の手札から合計4枚を選び、新しいシールドとして裏向きにし、自身のシールドゾーンに加える。<br/>このクリーチャーがシールドをブレイクする時、相手はそのシールドを自身の手札に加えるかわりに墓地に置く。<br/>T･ブレイカー<br/>エターナル・Ω'),
(2915, '真実の名ラララ・ライオネル', '0', '11,120', '9000', '7', '', '1,3', '', 'ブロッカー<br/>このクリーチャーをバトルゾーンに出した時、相手とガチンコ・ジャッジする。自分が勝ったら、自分の山札を見る。その中から、名前に《ヘブンズ》とある呪文を1枚選んですべてのプレイヤーに見せ、新しいシールドとして自分のシールドゾーンに裏向きにして加えてもよい。その後、山札をシャッフルする。<br/>W・ブレイカー'),
(2916, '黒神龍ダンチガイ・ファンキガイ', '2', '44,109', '6000', '6', '', '3', '2', 'このクリーチャーが攻撃する時、相手とガチンコ・ジャッジする。自分が勝ったら、進化ではないドラゴン・ゾンビを1体、自分の墓地からバトルゾーンに出してもよい。<br/>W・ブレイカー'),
(2917, '涙の終撃オニナグリ', '3', '58,109,107', '7000', '5', '1', '3', '2', '進化－自分のヒューマノイド1体の上に置く。<br/>このクリーチャーが攻撃する時、コストの合計が6以下になるように相手のクリーチャーを好きな数選び、破壊する。<br/>W・ブレイカー'),
(2918, '偽りの羅刹ミスディレクション', '0', '41,120', '9000', '8', '', '3', '2', '自分のコマンドが相手プレイヤーを攻撃してブロックされなかった時、自分の山札の上から1枚目を裏向きのまま、新しいシールドとして自分のシールドゾーンに加えてもよい。<br />W・ブレイカー'),
(2919, '合身巨兵エクスキュベーターMS', '1', '23', '7000', '5', '1', '3', '2', '進化－自分のグレートメカオー1体の上に置く。<br/>このクリーチャーが攻撃する時、相手とガチンコ・ジャッジする。自分が勝ったら、バトルゾーンにあるクリーチャーを1体選び、持ち主の手札に戻してもよい。その後、自分の山札の一番下のカードを、手札に加えてもよい。<br/>W・ブレイカー'),
(2920, '偽りの王ヴォルフガング', '3', '112,120', '8000', '9', '', '3', '2', 'このクリーチャーが攻撃する時、相手のパワー6000以下のクリーチャーを3体まで破壊する。<br/>W・ブレイカー'),
(2921, '真実の名ジャイアント・マック', '4', '68,120', '11000', '9', '', '3', '', 'このクリーチャーをバトルゾーンに出した時、バトルゾーンにある、このクリーチャーよりパワーの小さい相手のクリーチャーを1体選び、持ち主のマナゾーンに置く。その後、その選んだクリーチャーよりパワーの小さい、進化ではないジャイアントを1体、自分のマナゾーンからバトルゾーンに出してもよい。<br/>W・ブレイカー'),
(2922, '味頭領ドン・グリル', '4', '78', '9000', '5', '1', '18,3', '', '進化－自分のワイルド・ベジーズ1体の上に置く。<br/>G・ゼロ－バトルゾーンに自分のワイルド・ベジーズが4体以上あれば、このクリーチャーをコストを支払わずに召喚してもよい。<br/>W・ブレイカー'),
(2923, '偽りの羅刹 アリバイ・トリック', '0', '41,120', '8000', '8', '', '3', '', 'バトルゾーンにある相手のクリーチャーがタップされた時、バトルゾーンにある自分のクリーチャーを1体アンタップしてもよい。<br/>W・ブレイカー'),
(2924, '光器リスベット・ラヴィーン', '0', '18', '6000', '6', '', '1,3', '', 'ブロッカー<br/>このクリーチャーをバトルゾーンに出した時、相手とガチンコ・ジャッジする。自分が勝ったら、バトルゾーンにある相手のクリーチャーを2体まで選び、タップする。そのクリーチャーは次の相手のターンのはじめにアンタップされない。<br/>W・ブレイカー'),
(2925, 'トゥルー・ヘブンズ', '0', '0', '', '7', '', '', '1', 'S・トリガー<br/>バトルゾーンにある相手の進化ではないクリーチャーを1体選ぶ。バトルゾーンに自分のエンジェル・コマンドが5体以上あれば、バトルゾーンにある進化ではないクリーチャーをかわりに3体まで選ぶ。相手はその選ばれたクリーチャーを新しいシールドとして持ち主のシールドゾーンに裏向きにして加える。'),
(2926, 'サイバー・D・ゲーザ', '1', '99,109', '8000', '8', '', '3', '', 'このクリーチャーをバトルゾーンに出した時、バトルゾーンにある自分の、サイバー1体につき1枚、カードを引いてもよい。その後、バトルゾーンにある自分の、コマンド1体につき1枚、カードを引いてもよい。<br/>W・ブレイカー'),
(2927, '偽りの名 iFormulaX', '1', '23,120', '6000', '7', '', '3', '', '自分のターンの終わりに、このクリーチャーがタップされていて、自分の手札が10枚以上あれば、自分はゲームに勝利する。<br/>W・ブレイカー'),
(2928, '破壊機装デリート・タンク', '1', '23,123', '2000+', '4', '', '', '', '自分の手札の枚数が相手より多い場合、このクリーチャーのパワーは+6000され、「W・ブレイカー」を得る。'),
(2929, '偽りの星夜 ライス・シャワー', '2', '11,120', '11000', '8', '', '3', '', '光以外のエンジェル・コマンドが攻撃する時、自分のシールドをひとつ自分の墓地に置いてもよい。そうした場合、相手のクリーチャー1体と、そのクリーチャーと同じ名前を持つ相手のクリーチャーをすべて破壊する。<br/>W・ブレイカー'),
(2930, '邪龍秘伝ドラゴン・ボーン', '2', '0', '', '7', '', '', '', 'アタック・チャンス－ドラゴン・ゾンビ（自分のドラゴン・ゾンビが攻撃する時、この呪文をコストを支払わずに唱えてもよい）<br/>進化ではないドラゴン・ゾンビを1体、自分の墓地からバトルゾーンに出す。'),
(2931, 'ウェディング・ゲート', '2', '0', '', '6', '', '', '1', 'S・トリガー<br/>光でも進化でもないエンジェル・コマンドを2体まで、自分の手札からバトルゾーンに出す。'),
(2932, '偽りの名 シデン・ラゴン', '3', '50,120', '7000', '7', '', '3', '', '各ターン、このクリーチャーがはじめてタップされた時、自分の山札の上から1枚目をすべてのプレイヤーに見せる。そのカードがアンノウンであれば手札に加え、このクリーチャーをアンタップしてもよい。<br/>W・ブレイカー'),
